mapper.select:
select board_idx, title, writer, regdate, hit, notice from
(
select rownum rnum, c.* from (
select * from tb_complaints


	where ${scp1} like '%${search}%'



		or ${scp2} like '%${search}%'



		and regdate between #{from} and #{to}


order by notice desc, ${order} desc, ref desc, step asc
) c

) where rnum >= #{start} and rnum <= #{end}
